import React, { useState } from 'react';
import { Button, Modal, Form, Image, Row, Col } from 'react-bootstrap';
import './addMovie.css';

const AddMovie = ({ movieAdd }) => {
  const [show, setShow] = useState(false);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [posterUrl, setPosterUrl] = useState('');
  const [posterFile, setPosterFile] = useState(null);
  const [rating, setRating] = useState(3);
  const [genre, setGenre] = useState('');
  const [year, setYear] = useState('');
  const [errors, setErrors] = useState({});

  const reset = () => {
    setTitle('');
    setDescription('');
    setPosterUrl('');
    setPosterFile(null);
    setRating(3);
    setGenre('');
    setYear('');
    setErrors({});
  };

  const validate = () => {
    const err = {};
    if (!title.trim()) err.title = 'Le titre est requis';
    if (rating < 0 || rating > 5) err.rating = 'La note doit être entre 0 et 5';
    if (year) {
      const y = Number(year);
      const current = new Date().getFullYear();
      if (!Number.isInteger(y) || y < 1888 || y > current) err.year = `Année entre 1888 et ${current}`;
    }
    setErrors(err);
    return Object.keys(err).length === 0;
  };

  const readFileAsDataUrl = (file) =>
    new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onerror = () => {
        reader.abort();
        reject(new Error('Erreur lecture fichier'));
      };
      reader.onload = () => resolve(reader.result);
      reader.readAsDataURL(file);
    });

  const submitMovie = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    try {
      let finalPoster = posterUrl;
      if (posterFile) {
        finalPoster = await readFileAsDataUrl(posterFile);
      }
      const newMovie = {
        id: Date.now(),
        title: title.trim(),
        description: description.trim(),
        posterUrl: finalPoster || '',
        rating: Number(rating),
        genre: genre.trim(),
        year: year ? Number(year) : undefined,
      };
      movieAdd(newMovie);
      reset();
      setShow(false);
    } catch (err) {
      setErrors((prev) => ({ ...prev, poster: 'Impossible de lire l’image' }));
    }
  };

  return (
    <>
      <Button className="add-movie-button" variant="primary" onClick={() => setShow(true)}>
        + Add Movie
      </Button>

      <Modal show={show} onHide={() => { reset(); setShow(false); }} centered>
        <Form onSubmit={submitMovie} noValidate>
          <Modal.Header closeButton>
            <Modal.Title>Add a new movie</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form.Group className="mb-3" controlId="movieTitle">
              <Form.Label>Title *</Form.Label>
              <Form.Control
                type="text"
                placeholder="Movie title"
                value={title}
                isInvalid={!!errors.title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
              <Form.Control.Feedback type="invalid">{errors.title}</Form.Control.Feedback>
            </Form.Group>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3" controlId="movieGenre">
                  <Form.Label>Genre</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="e.g. Drama, Action"
                    value={genre}
                    onChange={(e) => setGenre(e.target.value)}
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3" controlId="movieYear">
                  <Form.Label>Year</Form.Label>
                  <Form.Control
                    type="number"
                    placeholder="e.g. 2023"
                    value={year}
                    isInvalid={!!errors.year}
                    onChange={(e) => setYear(e.target.value)}
                  />
                  <Form.Control.Feedback type="invalid">{errors.year}</Form.Control.Feedback>
                </Form.Group>
              </Col>
            </Row>

            <Form.Group className="mb-3" controlId="movieDesc">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Short description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="moviePosterUrl">
              <Form.Label>Poster URL (ou téléverser ci-dessous)</Form.Label>
              <Form.Control
                type="url"
                placeholder="https://..."
                value={posterUrl}
                isInvalid={!!errors.poster}
                onChange={(e) => setPosterUrl(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="moviePosterFile">
              <Form.Label>Upload poster (image)</Form.Label>
              <Form.Control
                type="file"
                accept="image/*"
                onChange={(e) => {
                  setPosterFile(e.target.files && e.target.files[0] ? e.target.files[0] : null);
                  // clear url if user uploads file
                  if (e.target.files && e.target.files[0]) setPosterUrl('');
                }}
              />
              <Form.Text className="text-muted">PNG, JPG, GIF — max 5MB recommended</Form.Text>
              <div className="image-preview mt-2">
                {posterFile ? (
                  <Image src={URL.createObjectURL(posterFile)} rounded fluid />
                ) : posterUrl ? (
                  <Image src={posterUrl} rounded fluid onError={() => setErrors((p) => ({ ...p, poster: 'URL invalide' }))} />
                ) : null}
              </div>
              <div className="text-danger small mt-1">{errors.poster}</div>
            </Form.Group>

            <Form.Group className="mb-0" controlId="movieRating">
              <Form.Label>Rating (0–5)</Form.Label>
              <Form.Control
                type="number"
                min="0"
                max="5"
                value={rating}
                isInvalid={!!errors.rating}
                onChange={(e) => setRating(Number(e.target.value))}
              />
              <Form.Control.Feedback type="invalid">{errors.rating}</Form.Control.Feedback>
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => { reset(); setShow(false); }}>
              Cancel
            </Button>
            <Button variant="primary" type="submit">
              Add Movie
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </>
  );
};

export default AddMovie;
