import React, { useState } from 'react';
import './addMovie.css';
import { Modal, Button, Form } from 'react-bootstrap';

const AddMovie = () => {
  // Ã‰tat pour afficher ou masquer le modal
  const [show, setShow] = useState(false);

  // Ã‰tats pour les champs du formulaire
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [posterUrl, setPosterUrl] = useState('');
  const [rate, setRate] = useState(1);

  // Ouvrir et fermer le modal
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  // Soumission du formulaire
  const handleSubmit = (e) => {
    e.preventDefault();

    const newMovie = {
      title,
      description,
      posterUrl,
      rate,
    };

    console.log('ðŸŽ¬ Nouveau film ajoutÃ© :', newMovie);

    // RÃ©initialiser les champs
    setTitle('');
    setDescription('');
    setPosterUrl('');
    setRate(1);
    handleClose();
  };

  return (
    <div className="add-movie-container text-center mt-4">
      <Button variant="primary" onClick={handleShow}>
        Ajouter un film
      </Button>

      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>Ajouter un film</Modal.Title>
        </Modal.Header>

        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            <Form.Group className="mb-3">
              <Form.Label>Titre</Form.Label>
              <Form.Control
                type="text"
                placeholder="Entrez le titre"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Entrez une description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>URL de l'affiche</Form.Label>
              <Form.Control
                type="text"
                placeholder="https://..."
                value={posterUrl}
                onChange={(e) => setPosterUrl(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Note (1 Ã  5)</Form.Label>
              <Form.Control
                type="number"
                min="1"
                max="5"
                value={rate}
                onChange={(e) => setRate(Number(e.target.value))}
              />
            </Form.Group>
          </Modal.Body>

          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Annuler
            </Button>
            <Button variant="success" type="submit">
              Enregistrer
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </div>
  );
};

export default AddMovie;
