import React, { useState } from 'react';
import { Rate, Space } from 'antd';
import 'antd/dist/reset.css';

const desc = ['terrible', 'bad', 'normal', 'good', 'wonderful'];

const Rating = ({ value, onChange, showLabel = true, defaultValue = 0 }) => {
  const [internalValue, setInternalValue] = useState(defaultValue);
  const currentValue = value ?? internalValue;

  const handleChange = (newValue) => {
    const normalized = newValue || 0;
    if (onChange) onChange(normalized);
    if (value === undefined) setInternalValue(normalized);
  };

  return (
    <Space direction="vertical" size="small" style={{ alignItems: 'center' }}>
      <Rate tooltips={desc} onChange={handleChange} value={currentValue} allowClear />
      {showLabel && currentValue ? (
        <small style={{ color: '#fff' }}>{desc[currentValue - 1]}</small>
      ) : null}
    </Space>
  );
};

export default Rating;
