import React, { useState } from 'react';
import './addMovie.css';
import { Modal, Button, Form } from 'react-bootstrap';

const AddMovie = ({ movieAdd }) => {
  // État pour contrôler l'affichage du modal
  const [show, setShow] = useState(false);

  // Fonctions pour ouvrir et fermer le modal
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  // États pour stocker les données du formulaire
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [posterUrl, setPosterUrl] = useState('');
  const [rate, setRate] = useState(1);
// Fonction appelée lors de la soumission du formulaire
const handleSubmit = (e) => {
  // Empêche le rechargement de la page lors de la soumission
  e.preventDefault();

  // Crée un nouvel objet film avec les données du formulaire
  movieAdd({
    id: Date.now(),           // Génère un identifiant unique basé sur le timestamp
    title,                    // Titre du film
    description,              // Description du film
    posterUrl,                // URL de l'affiche du film
    rate                      // Note du film
  });

  // Réinitialise les champs du formulaire après l'ajout
  setTitle('');
  setDescription('');
  setPosterUrl('');
  setRate(1);

  // Ferme le modal après la soumission
  handleClose();
};

  return (
    <div>
      {/* Bouton pour afficher le modal */}
      <Button variant="primary" onClick={handleShow}>
        Add Movie
      </Button>

      {/* Modal contenant le formulaire d'ajout */}
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Add Movie</Modal.Title>
        </Modal.Header>

        {/* Formulaire d'ajout de film */}
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            {/* Champ pour le titre du film */}
            <Form.Group className="mb-3">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </Form.Group>

            {/* Champ pour la description du film */}
            <Form.Group className="mb-3">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Enter description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </Form.Group>

            {/* Champ pour la note du film */}
            <Form.Group className="mb-3">
              <Form.Label>Rate</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter rate"
                value={rate}
                onChange={(e) => setRate(e.target.value)}
              />
            </Form.Group>

            {/* Champ pour l'URL de l'affiche */}
            <Form.Group className="mb-3">
              <Form.Label>Poster URL</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter poster URL"
                value={posterUrl}
                onChange={(e) => setPosterUrl(e.target.value)}
              />
            </Form.Group>
          </Modal.Body>

        {/* Boutons de fermeture et de sauvegarde */}
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          {/* Ce bouton devrait idéalement déclencher une fonction de sauvegarde si tous les champs sont remplis */}
          <Button variant="primary" type="submit" onClick={handleClose} disabled={!title || !description || !posterUrl || !rate}>
            Save Changes
          </Button>
        </Modal.Footer>
        </Form>
      </Modal>
    </div>
  );
};

export default AddMovie;
