import { useState } from 'react';
import './App.css';
import './components/addmovie/addMovie.css';
import { Container, Row, Col, Navbar } from 'react-bootstrap';
import MovieList from './components/MovieList';
import AddMovie from './components/addmovie/AddMovie';
import { moviesData } from './movieDATA';
import FilterByTitle from './components/FilterByTitle';
import Rating from './components/Rating';

function App() {
  const [movies, setMovies] = useState(moviesData);
  const [searchTitle, setSearchTitle] = useState('');
  const [filterRate, setFilterRate] = useState(0);

  const movieAdd = (newMovie) => {
    setMovies((prev) => [...prev, newMovie]);
  };

  return (
    <div className="App">
      <Navbar bg="dark" variant="dark" className="mb-4">
        <Container>
          <Navbar.Brand>ðŸŽ¬ My Movie App</Navbar.Brand>
        </Container>
      </Navbar>

      {/* Barre de filtrage */}
      <Container>
        <Row className="justify-content-center mb-3 g-3">
          <Col xs={12} lg={6}>
            <FilterByTitle value={searchTitle} onChange={setSearchTitle} />
          </Col>

          <Col xs={12} lg={3} className="d-flex justify-content-center">
            <div className="filter-rating-card">
              <span className="filter-rating-title">Filtrer par note</span>
              <Rating value={filterRate} onChange={setFilterRate} showLabel={false} />
              <button
                type="button"
                className="filter-rating-reset"
                onClick={() => setFilterRate(0)}
              >
                RÃ©initialiser
              </button>
            </div>
          </Col>
        </Row>
      </Container>

      {/* Contenu principal */}
      <Container>
        <Row className="text-center mb-4">
          <Col>
            <h1 className="text-primary">Welcome to My Movie App</h1>
            <p className="text-muted">Explore and enjoy your favorite movies!</p>
          </Col>
        </Row>

        <Row className="justify-content-center mb-4">
          <Col xs="auto">
            <AddMovie movieAdd={movieAdd} />
          </Col>
        </Row>

        <Row>
          <MovieList movies={movies} searchTitle={searchTitle} filterRate={filterRate} />
        </Row>
      </Container>
    </div>
  );
}

export default App;
