import React from 'react';
import { Form } from 'react-bootstrap';

const FilterByRate = ({ value = 0, onChange }) => {
  return (
    <Form className="mb-3 filter-by-rate" aria-label="Filtrer par note">
      <Form.Select
        value={value}
        onChange={(e) => onChange(Number(e.target.value))}
        aria-label="Sélectionner note minimale"
      >
        <option value={0}>Toutes les notes</option>
        <option value={1}>★ et plus</option>
        <option value={2}>★★ et plus</option>
        <option value={3}>★★★ et plus</option>
        <option value={4}>★★★★ et plus</option>
        <option value={5}>★★★★★</option>
      </Form.Select>
    </Form>
  );
};

export default FilterByRate;
