import React, { useState } from 'react';
import './addMovie.css';
import { Modal, Button, Form } from 'react-bootstrap';

const AddMovie = () => {
  // État pour afficher ou cacher le modal
  const [show, setShow] = useState(false);

  // États pour les champs du formulaire
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [posterUrl, setPosterUrl] = useState('');

  // Fonctions pour ouvrir et fermer le modal
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  // Fonction de soumission du formulaire
  const handleSubmit = (e) => {
    e.preventDefault();

    // Exemple de traitement des données
    const newMovie = {
      title,
      description,
      posterUrl,
    };

    console.log('Film ajouté :', newMovie);

    // Réinitialiser le formulaire et fermer le modal
    setTitle('');
    setDescription('');
    setPosterUrl('');
    handleClose();
  };

  return (
    <div className="add-movie-container">
      {/* Bouton pour ouvrir le modal */}
      <Button variant="primary" onClick={handleShow}>
        Ajouter un film
      </Button>

      {/* Modal contenant le formulaire */}
      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>Ajouter un nouveau film</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3" controlId="formTitle">
              <Form.Label>Titre du film</Form.Label>
              <Form.Control
                type="text"
                placeholder="Entrez le titre"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Entrez une description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formPosterUrl">
              <Form.Label>URL de l'affiche</Form.Label>
              <Form.Control
                type="text"
                placeholder="https://..."
                value={posterUrl}
                onChange={(e) => setPosterUrl(e.target.value)}
              />
            </Form.Group>

            <Button variant="success" type="submit">
              Enregistrer
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default AddMovie;
