// FilterByTitle component allows users to filter movies by title
import React from 'react';
import { Form, InputGroup } from 'react-bootstrap';

const FilterByTitle = ({ value, onChange, placeholder = 'Filtrer par titre...' }) => {
  return (
    <Form className="mb-3 filter-by-title">
      <InputGroup>
        <Form.Control
          type="search"
          placeholder={placeholder}
          value={value}
          onChange={(e) => onChange(e.target.value)}
        />
      </InputGroup>
    </Form>
  );
};

export default FilterByTitle;
