import React, { useState } from 'react';
import MovieCard from './MovieCard';
import { Container, Row, Col, Button, Badge } from 'react-bootstrap';
import FilterByTitle from './FilterByTitle';

// Styles intÃ©grÃ©s
const styles = {
  section: {
    padding: '40px 0',
    backgroundColor: '#f8f9fa',
  },
  title: {
    textAlign: 'center',
    color: '#0d6efd',
    marginBottom: '20px',
    fontWeight: 'bold',
  },
};

const MovieList = ({ movies, searchTitle }) => {
  const [filterText, setFilterText] = useState('');

  const displayedMovies = movies.filter((movie) => {
    const title = (movie.title || '').toString().toLowerCase();
    const search = (searchTitle || '').toString().toLowerCase();
    const filter = (filterText || '').toString().toLowerCase();
    return title.includes(search) && title.includes(filter);
  });

  return (
    <div style={styles.section}>
      <Container>
        <h2 style={styles.title}>ðŸŽ¬ Movie List</h2>

        {/* Filtre et contrÃ´les */}
        <Row className="align-items-center mb-3">
          <Col xs={12} md={8}>
            <FilterByTitle value={filterText} onChange={setFilterText} />
          </Col>
          <Col xs={12} md={4} className="text-md-end mt-2 mt-md-0">
            <Badge bg="secondary" className="me-2">
              {displayedMovies.length} rÃ©sultat{displayedMovies.length > 1 ? 's' : ''}
            </Badge>
            <Button
              variant="outline-secondary"
              size="sm"
              onClick={() => setFilterText('')}
            >
              Effacer
            </Button>
          </Col>
        </Row>

        {/* Grille responsive */}
        <Row className="g-4 justify-content-center">
          {displayedMovies.map((movie) => (
            <Col key={movie.id} xs={12} sm={10} md={6} lg={4} xl={3}>
              <MovieCard movie={movie} />
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default MovieList;
