import { useState } from 'react';
import './App.css';
import { Container, Row, Col, Form, InputGroup, Navbar } from 'react-bootstrap';
import MovieList from './components/MovieList';
import AddMovie from './components/AddMovie';
import { moviesData } from './movieDATA';
import FilterByTitle from './components/FilterByTitle';

function App() {
  const [movies, setMovies] = useState(moviesData);

  // Fonction de recherche par titre
  const searchMovie = (title) => {
    const filteredMovies = moviesData.filter((movie) =>
      movie.title.toLowerCase().includes(title.toLowerCase())
    );
    setMovies(filteredMovies);
  }
// fonction filtrage
  const filterMovies = (filterTerm) => {
    const filteredMovies = moviesData.filter((movie) =>
      movie.title.toLowerCase().includes(filterTerm.toLowerCase())
    );
    setMovies(filteredMovies);
  };

  // Fonction d'ajout
  const movieAdd = (newMovie) => {
    setMovies([...movies, newMovie]);
  };

  return (
    <div className="App">
      {/* Barre de navigation */}
      <Navbar bg="dark" variant="dark" className="mb-4">
        <Container>
          <Navbar.Brand>ðŸŽ¬ My Movie App</Navbar.Brand>
        </Container>
      </Navbar>

      {/* Contenu principal */}
      <Container>
        <Row className="text-center mb-4">
          <Col>
            <h1 className="text-primary">Welcome to My Movie App</h1>
            <p className="text-muted">Explore and enjoy your favorite movies!</p>
          </Col>
        </Row>



        {/* Barre de filtrage */}
        <Row className="text-center mb-4">
          <Col>
            <FilterByTitle movies={movies} searchTitle={searchTitle} setSearchTitle={setSearchTitle} />
          </Col>
        </Row>  



        {/* Barre de recherche */}
        <Row className="justify-content-center mb-4">
          <Col xs={12} md={6}>
            <InputGroup>
              <Form.Control
                type="text"
                placeholder="Search for a movie..."
                onChange={(e) => searchMovie(e.target.value)}
              />
            </InputGroup>
          </Col>
        </Row>

        {/* Composant d'ajout de film */}
        <Row className="justify-content-center mb-4">
          <Col xs="auto">
            <AddMovie movieAdd={movieAdd} />
          </Col>
        </Row>

        {/* Liste des films */}
        <Row>
          <MovieList movies={movies} />
        </Row>
      </Container>
    </div>
  );
}

export default App;
