import { useState } from 'react';
import './App.css';
import './components/addmovie/addMovie.css';
import { Container, Row, Col, Form, InputGroup, Navbar, Modal, Button } from 'react-bootstrap';
import MovieList from './components/MovieList';
import AddMovie from './components/addmovie/AddMovie';
import { moviesData } from './movieDATA';
import FilterByTitle from './components/FilterByTitle';
import Rating from './components/Rating';

function App() {
  const [movies, setMovies] = useState(moviesData);

  // recherche par titre
  const [searchTitle, setSearchTitle] = useState('');
  const [filterRate, setFilterRate] = useState(0);
  const searchMovie = (value) => setSearchTitle(value);

  // ajout
  const movieAdd = (newMovie) => {
    setMovies([...movies, newMovie]);
  };

  // Modal state
  const [show, setShow] = useState(false);

  // Form states
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [posterUrl, setPosterUrl] = useState('');
  const [rating, setRating] = useState(0);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newMovie = { title, description, posterUrl, rating };
    movieAdd(newMovie);
    reset();
    setShow(false);
  };

  const reset = () => {
    setTitle('');
    setDescription('');
    setPosterUrl('');
    setRating(0);
  };

  return (
    <div className="App">
      <Navbar bg="dark" variant="dark" className="mb-4">
        <Container>
          <Navbar.Brand>ðŸŽ¬ My Movie App</Navbar.Brand>
        </Container>
      </Navbar>

      {/* Barre de filtrage */}
      <Container>
        <Row className="justify-content-center mb-3">
          <Col xs={12} md={6}>
            <FilterByTitle value={searchTitle} onChange={setSearchTitle} />
          </Col>

          <Col xs={12} md={3}>
            <R value={filterRate} onChange={setFilterRate} />
          </Col>

          <Col xs={12} md={3} className="d-flex align-items-center justify-content-center">
            <Rating />
          </Col>
        </Row>
      </Container>

      {/* Contenu principal */}
      <Container>
        <Row className="text-center mb-4">
          <Col>
            <h1 className="text-primary">Welcome to My Movie App</h1>
            <p className="text-muted">Explore and enjoy your favorite movies!</p>
          </Col>
        </Row>

        <Row className="justify-content-center mb-4">
          <Col xs="auto">
            <Button variant="primary add-movie-button" onClick={() => setShow(true)}>
              Add Movie
            </Button>
          </Col>
        </Row>

        {/* Liste des films */}
        <Row>
          <MovieList movies={movies} searchTitle={searchTitle} filterRate={filterRate} />
        </Row>
      </Container>

      {/* Modal pour ajouter un film */}
      <Modal show={show} onHide={() => setShow(false)} centered>
        <Form onSubmit={handleSubmit}>
          <Modal.Header closeButton>
            <Modal.Title>Add a new movie</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form.Group className="mb-3" controlId="movieTitle">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="text"
                placeholder="Movie title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="movieDesc">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                placeholder="Short description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="moviePoster">
              <Form.Label>Poster URL</Form.Label>
              <Form.Control
                type="url"
                placeholder="https://..."
                value={posterUrl}
                onChange={(e) => setPosterUrl(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-0" controlId="movieRating">
              <Form.Label>Rating</Form.Label>
              <Form.Control
                type="number"
                min="0"
                max="5"
                value={rating}
                onChange={(e) => setRating(Number(e.target.value))}
              />
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => { reset(); setShow(false); }}>
              Cancel
            </Button>
            <Button variant="primary" type="submit">
              Add Movie
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </div>
  );
}

export default App;
