import React, { useState } from 'react';
import MovieCard from './MovieCard';
import { Container, Row, Col } from 'react-bootstrap';

// Styles intÃ©grÃ©s
const styles = {
  section: {
    padding: '40px 0',
    backgroundColor: '#f8f9fa',
  },
  title: {
    textAlign: 'center',
    color: '#0d6efd',
    marginBottom: '30px',
    fontWeight: 'bold',
  },
};

const MovieList = ({ movies, searchTitle, filterRate = 0 }) => {
  const [filterText] = useState('');

  const displayedMovies = movies.filter((movie) => {
    const title = (movie.title || '').toString().toLowerCase();
    const search = (searchTitle || '').toString().toLowerCase();
    const filter = (filterText || '').toString().toLowerCase();

    const movieRating =
      Number(movie.rating ?? movie.rate ?? movie.Rating ?? movie.stars ?? 0) || 0;

    const matchesTitle = title.includes(search) && title.includes(filter);
    const matchesRate = filterRate > 0 ? movieRating >= filterRate : true;

    return matchesTitle && matchesRate;
  });

  return (
    <div style={styles.section}>
      <Container>
        <h2 style={styles.title}>ðŸŽ¬ Movie List</h2>

        {/* Grille responsive */}
        <Row className="g-4 justify-content-center">
          {displayedMovies.length === 0 ? (
            <Col xs={12} className="text-center text-muted py-5">
              Aucun film ne correspond Ã  ces critÃ¨res.
            </Col>
          ) : (
            displayedMovies.map((movie) => (
              <Col key={movie.id} xs={12} sm={10} md={6} lg={4} xl={3}>
                <MovieCard movie={movie} />
              </Col>
            ))
          )}
        </Row>
      </Container>
    </div>
  );
};

export default MovieList;
